import { useState,useEffect } from "react";
import '../styles/bookList.css'

const BookList = () => {
    let[books,setBooks] = useState([])

     useEffect( ()=>{
        let fetchData = async() =>{
            let response = await fetch("http://localhost:8000/books")
            let data = await response.json()
            setBooks(data)
        }
        fetchData()
    },[])

     let handleDelete = (id,title) =>{
        setBooks( books.filter( x => x.id != id ) )
        alert`${title} has been deleted`
     }

    return ( 
        <div className="bookList">
            <h1>Book List: {books.length}</h1>
            <div className="book_section">
                { books.map(data => (
                    <div className="book_card">
                        <div className="book_image">
                               <img src={data.thumbnailUrl} alt="" />
                        </div>
                        <div className="book_details">
                               <h2 style={{color:"green"}}> <b>Title:</b> {data.title}, {data.pageCount} Pages</h2>
                               <p> <b>Authors:</b> {data.authors.toString()} </p>
                               <p> <b>Category:</b> {data.categories.toString()} </p>
                               <button onClick={()=> handleDelete(data.id,data.title)} >Delete</button>
                        </div>
                    </div>
                )) }
            </div>
        </div>
     );
}
 
export default BookList;
